&lt;span class=&#34;line&#34;&gt;	cout &amp;lt;&amp;lt; endl;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	cout &amp;lt;&amp;lt; &lt;span class=&#34;built_in&#34;&gt;lexicographical_compare&lt;/span&gt;(v.&lt;span class=&#34;built_in&#34;&gt;begin&lt;/span&gt;(), v.&lt;span class=&#34;built_in&#34;&gt;end&lt;/span&gt;(), v2.&lt;span class=&#34;built_in&#34;&gt;begin&lt;/span&gt;(), v2.&lt;span class=&#34;built_in&#34;&gt;end&lt;/span&gt;()) &amp;lt;&amp;lt; endl;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&lt;span class=&#34;comment&#34;&gt;// 按字典顺序比较 // 1&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&lt;span class=&#34;keyword&#34;&gt;return&lt;/span&gt; &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
&lt;h3 id=&#34;数值算法&#34;&gt;数值算法&lt;a title=&#34;#数值算法&#34; href=&#34;#数值算法&#34;&gt;&lt;/a&gt;&lt;/h3&gt;
&lt;p&gt;STL 提供了 个通用数值算法。为了调用此类算法，需要包含头文件&lt;numeric&gt;。&lt;/p&gt;
