;line&#34;&gt;    &lt;span class=&#34;type&#34;&gt;double&lt;/span&gt;  x;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    cout &amp;lt;&amp;lt; &lt;span class=&#34;string&#34;&gt;&amp;quot;input  a  number:&amp;quot;&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    cin &amp;gt;&amp;gt; x;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    cout &amp;lt;&amp;lt; &lt;span class=&#34;string&#34;&gt;&amp;quot;the  result  of  sqrt  is:&amp;quot;&lt;/span&gt; &amp;lt;&amp;lt; myMath::&lt;span class=&#34;built_in&#34;&gt;mysqrt&lt;/span&gt;(x) &amp;lt;&amp;lt; endl;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    cout &amp;lt;&amp;lt; &lt;span class=&#34;string&#34;&gt;&amp;quot;the  result  of  sin  is:&amp;quot;&lt;/span&gt; &amp;lt;&amp;lt; myMath::&lt;span class=&#34;built_in&#34;&gt;mysin&lt;/span&gt;(x) &amp;lt;&amp;lt; endl;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    cout &amp;lt;&amp;lt; &lt;span class=&#34;string&#34;&gt;&amp;quot;the  result  of  fabs  is:&amp;quot;&lt;/span&gt; &amp;lt;&amp;lt; myMath::&lt;span class=&#34;built_in&#34;&gt;myfabs&lt;/span&gt;((&lt;span class=&#34;type&#34;&gt;int&lt;/span&gt;)x) &amp;lt;&amp;lt; endl;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    cout &amp;lt;&amp;lt; &lt;span class=&#34;string&#34;&gt;&amp;quot;the  result  of  fabs  is:&amp;quot;&lt;/span&gt; &amp;lt;&amp;lt; myMath::&lt;span class=&#34;built_in&#34;&gt;myfabs&lt;/span&gt;(x) &amp;lt;&amp;lt; endl;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    cout &amp;lt;&amp;lt; &lt;span class=&#34;string&#34;&gt;&amp;quot;the  result  of  area  is:&amp;quot;&lt;/span&gt; &amp;lt;&amp;lt; myMath::&lt;span class=&#34;built_in&#34;&gt;circleArea&lt;/span&gt;(x) &amp;lt;&amp;lt; endl;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;keyword&#34;&gt;return&lt;/span&gt;  &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
&lt;p&gt;编写工具类, 使用静态方法. 构造函数最好给成private,不能实例化对象. 只能通过类名调用&lt;/p&gt;
&lt;h2 id=&#34;task5_1&#34;&gt;task5_1&lt;a title=&#34;#task5_1&#34; href=&#34;#task5_1&#34;&gt;&lt;/a&gt;&lt;/h2&gt;
