 &lt;use xlink:href=&#34;#E1-MJMAIN-3D&#34; x=&#34;1466&#34; y=&#34;0&#34;&gt;&lt;/use&gt;
&lt;g transform=&#34;translate(2522,0)&#34;&gt;
 &lt;use xlink:href=&#34;#E1-MJMATHI-74&#34; x=&#34;0&#34; y=&#34;0&#34;&gt;&lt;/use&gt;
 &lt;use xlink:href=&#34;#E1-MJMAIN-5B&#34; x=&#34;361&#34; y=&#34;0&#34;&gt;&lt;/use&gt;
