 &lt;span class=&#34;operator&#34;&gt;=&lt;/span&gt; orderitems.order_num&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;INNER&lt;/span&gt; &lt;span class=&#34;keyword&#34;&gt;JOIN&lt;/span&gt; customers &lt;span class=&#34;keyword&#34;&gt;ON&lt;/span&gt; customers.cust_id &lt;span class=&#34;operator&#34;&gt;=&lt;/span&gt; orders.cust_id&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;GROUP&lt;/span&gt; &lt;span class=&#34;keyword&#34;&gt;BY&lt;/span&gt; cust_name &lt;span class=&#34;keyword&#34;&gt;HAVING&lt;/span&gt; &lt;span class=&#34;built_in&#34;&gt;SUM&lt;/span&gt;(item_price&lt;span class=&#34;operator&#34;&gt;*&lt;/span&gt;quantity) &lt;span class=&#34;operator&#34;&gt;&amp;gt;=&lt;/span&gt; &lt;span class=&#34;number&#34;&gt;1000&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;SELECT&lt;/span&gt; cust_name, &lt;span class=&#34;built_in&#34;&gt;SUM&lt;/span&gt;(item_price&lt;span class=&#34;operator&#34;&gt;*&lt;/span&gt;quantity) &lt;span class=&#34;keyword&#34;&gt;AS&lt;/span&gt; total_price&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;FROM&lt;/span&gt; orderitems, orders, customers&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;WHERE&lt;/span&gt; orders.order_num &lt;span class=&#34;operator&#34;&gt;=&lt;/span&gt; orderitems.order_num &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;AND&lt;/span&gt; customers.cust_id &lt;span class=&#34;operator&#34;&gt;=&lt;/span&gt; orders.cust_id&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;GROUP&lt;/span&gt; &lt;span class=&#34;keyword&#34;&gt;BY&lt;/span&gt; cust_name &lt;span class=&#34;