;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&lt;span class=&#34;function&#34;&gt;string &lt;span class=&#34;title&#34;&gt;s_5&lt;/span&gt;&lt;span class=&#34;params&#34;&gt;(s_1, &lt;span class=&#34;number&#34;&gt;3&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;3&lt;/span&gt;)&lt;/span&gt;&lt;/span&gt;;		&lt;span class=&#34;comment&#34;&gt;// bar&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	cout &amp;lt;&amp;lt; s_5 &amp;lt;&amp;lt; endl;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&lt;span class=&#34;comment&#34;&gt;// string(const string&amp;amp; rhs, unsigned int pos, unsigned int n) ;&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&lt;span class=&#34;function&#34;&gt;string &lt;span class=&#34;title&#34;&gt;s_6&lt;/span&gt;&lt;span class=&#34;params&#34;&gt;(s_1, &lt;span class=&#34;number&#34;&gt;5&lt;/span&gt;)&lt;/span&gt;&lt;/span&gt;;			&lt;span class=&#34;comment&#34;&gt;// r&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	cout &amp;lt;&amp;lt; s_6 &amp;lt;&amp;lt; endl;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&lt;span class=&#34;function&#34;&gt;string &lt;span class=&#34;title&#34;&gt;s_7&lt;/span&gt;&lt;span class=&#34;params&#34;&gt;(&lt;span class=&#34;number&#34;&gt;10&lt;/span&gt;, &lt;span class=&#34;string&#34;&gt;&amp;#x27;c&amp;#x27;&lt;/span&gt;)&lt;/span&gt;&lt;/span&gt;;		&lt;span class=&#34;comment&#34;&gt;// cccccccccc&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	cout &amp;lt;&amp;lt; s_7 &amp;lt;&amp;lt; endl;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	cout &amp;lt;&amp;lt; &lt;span class=&#34;built_in&#34;&gt;string&lt;/span&gt;(&lt;span class=&#34;string&#34;&gt;&amp;quot;foo&amp;quot;&lt;/span&gt;) + &lt;span class=&#34;built_in&#34;&gt;string&lt;/span&gt;(&lt;span class=&#34;string&#34;&gt;&amp;quot;bar&amp;quot;&lt;/span&gt;) &amp;lt;&amp;lt; endl;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	cout &amp;lt;&amp;lt; (&lt;span class=&#34;built_in&#34;&gt;string&lt;/span&gt;(&lt;span class=&#34;string&#34;&gt;&amp;quot;aaa&amp;quot;&lt;/span&gt;) &amp;lt; &lt;span class=&#34;built_in&#34;&gt;string&lt;/span&gt;(&lt;span class=&#34;string&#34;&gt;&amp;quot;ddd&amp;quot;&lt;/span&gt;) )&amp;lt;&amp;lt; endl;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	cout &amp;lt;&amp;lt; &lt;span class=&#34;built_in&#34;&gt;string&lt;/span&gt;(&lt;span class=&#34;string&#34;&gt;&amp;quot;012345&amp;quot;&lt;/span&gt;)[&lt;span class=&#34;number&#34;&gt;3&lt;/span&gt;] &amp;lt;&amp;lt; endl;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&lt;span class=&#34;comment&#34;&gt;// 常用方法&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	cout &amp;lt;&amp;lt; &lt;span class=&#34;built_in&#34;&gt;string&lt;/span&gt;(&lt;span class=&#34;string&#34;&gt;&amp;quot;g&amp;quot;&lt;/span&gt;).&lt;span class=&#34;built_in&#34;&gt;append&lt;/span&gt;(&lt;span class=&#34;string&#34;&gt;&amp;quot;o&amp;quot;&lt;/span&gt;).&lt;span class=&#34;built_in&#34;&gt;append&lt;/span&gt;(&lt;span class=&#34;string&#34;&gt;&amp;quot;o&amp;quot;&lt;/span&gt;).&lt;span class=&#34;built_in&#34;&gt;append&lt;/span&gt;(&lt;span class=&#34;string&#34;&gt;&amp;quot;d&amp;quot;&lt;/span&gt;) &amp;lt;&amp;lt; endl;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	cout &amp;lt;&amp;lt; &lt;span class=&#34;built_in&#34;&gt;string&lt;/span&gt;(&lt;span class=&#34;string&#34;&gt;&amp;quot;01234567&amp;quot;&lt;/span&gt;).&lt;span class=&#34;built_in&#34;&gt;substr&lt;/span&gt;(&lt;span class=&#34;number&#34;&gt;3&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;2&lt;/span&gt;) &amp;lt;&amp;lt; endl;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	cout &amp;lt;&amp;lt; &lt;span class=&#34;built_in&#34;&gt;string&lt;/span&gt;(&lt;span class=&#34;string&#34;&gt;&amp;quot;012345&amp;quot;&lt;/span&gt;).&lt;span class=&#34;built_in&#34;&gt;find&lt;/span&gt;(&lt;span class=&#34;string&#34;&gt;&amp;quot;3&amp;quot;&lt;/span&gt;) &amp;lt;&amp;lt; endl;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	cout &amp;lt;&amp;lt; &lt;span class=&#34;built_in&#34;&gt;string&lt;/span&gt;(&lt;span class=&#34;string&#34;&gt;&amp;quot;123&amp;quot;&lt;/span&gt;).&lt;span class=&#34;built_in&#34;&gt;length&lt;/span&gt;() &amp;lt;&amp;lt; endl;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	s_1.&lt;span class=&#34;built_in&#34;&gt;swap&lt;/span&gt;(s_2);		&lt;span class=&#34;comment&#34;&gt;// s_1 指向 s_2&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	cout &amp;lt;&amp;lt; s_1 &amp;lt;&amp;lt; &lt;span class=&#34;string&#34;&gt;&amp;quot;   &amp;quot;&lt;/span&gt; &amp;lt;&amp;lt; s_2 &amp;lt;&amp;lt; endl;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&lt;span class=&#34;comment&#34;&gt;// ....&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&lt;span class=&#34;keyword&#34;&gt;return&lt;/span&gt; &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
&lt;h2 id=&#34;指针与引用&#34;&gt;指针与引用&lt;a title=&#34;#指针与引用&#34; href=&#34;#指针与引用&#34;&gt;&lt;/a&gt;&lt;/h2&gt;
&lt;p&gt;所以说指针是一种底层的机制。引用则是一种较高层的机制，在语言概念上它是另一变量的&amp;quot;别名&amp;quot;，把地址这一概念隐藏起来了，但在引用运行时的实现机制中，还不得不借助于地址。二者可以说是殊途同归，差异主要是语言形式, 最后都是靠存储地址来实现的。&lt;code&gt;引用相当于指针常量, 引用是为了方便使用对指针的包装&lt;/code&gt;.&lt;/p&gt;
&lt;p&gt;有些时候引用不能代替使用指针:&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;使用函数指针，由于没有函数引用，所以函数指针无法被引用替代。&lt;/li&gt;
&lt;li&gt;用 new 动态创建的对象或数组，需要用指针来存储它的地址。&lt;/li&gt;
&lt;li&gt;以数组形式传递大批量数据时，需要用指针类型接收参数&lt;/li&gt;
&lt;/ul&gt;
&lt;p&gt;&lt;code&gt;T &amp;amp;s = *(new T()); delete &amp;amp;s;&lt;/code&gt;, 这样其实可以, 一般不用.&lt;/p&gt;
&lt;h3 id=&#34;指针类型安全性&#34;&gt;指针类型安全性&lt;a title=&#34;#指针类型安全性&#34; href=&#34;#指针类型安全性&#34;&gt;&lt;/a&gt;&lt;/h3&gt;
&lt;p&gt;&lt;code&gt;reinterpret_cast&lt;/code&gt;可以将一种类型的指针转换为另一种类型的指针. 具有很大的危险性和不确定性，c++ 标准只保证用 reinterpret_cast 转换后与再转换回的值相同.&lt;/p&gt;
&lt;p&gt;C 允许 void 指针隐含地转换为其他任何类型的指针，而 c++ 规定这种情况只能显式转换，这是 c++ 相比的一个安全之处。&lt;/p&gt;
&lt;p&gt;void 指针的另一个用途在于，有时一个指针可能会指向不同类型的对象， void 指针只起一定的传递作用，最终使用该指针时，还需要根据情况将指针还原为它原先的类型。&lt;/p&gt;
&lt;p&gt;有很多从C标准 继承而来的函数会使用 void 指针作为参数和返回值，例如将二段内存空间设为一个固定值 (memset) 、比较两段内存空间 (memcmp) 、复制一段内存空间(memcpy) 、动态分配一段内存空间 (malloc) 、释放动态分配的内存空间 (free) 等，这些操作都是不管具体的数据类型，把不同类型的数据当作无差别的二进制序列。其中，动态内存管理的函数( malloc free 等)已经可以被 c++ new delete 关键字全面替代，而直接内存操作的函数 (memset memcmp , memcpy 等)只能针对对象的二进制表示进行处理，不符合面向对象的要求，一般不用，至多对一些基本数据类型的数组使用。&lt;/p&gt;
&lt;p&gt;总结起来，保证指针类型安全性的办法有以下几种。&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;除非非常特殊的底层用途， reinterprt_cast 不要用。&lt;/li&gt;
&lt;li&gt;继承标准 的涉及 void 指针的函数，一般不要用，至多对一些基本数据类型及其数组使用。&lt;/li&gt;
&lt;li&gt;如果一定需要用 void 指针，那么用 static_cast void 指针转换为具体类型的指针时，一定要转换为最初的类型(即当初转换到该 void 指针的指针类型)。&lt;/li&gt;
&lt;/ul&gt;
&lt;h3 id=&#34;堆对象管理&#34;&gt;堆对象管理&lt;a title=&#34;#堆对象管理&#34; href=&#34;#堆对象管理&#34;&gt;&lt;/a&gt;&lt;/h3&gt;
&lt;p&gt;通常使用的局部变量，在运行栈上分配空间，空间分配和释放的过程是由编译器生成的代码控制的，一个函数返回后相应的空间会自行释放;而静态生存期变量，其空间的分配是由连接器完成的，它们占用的空间大小始终是固定的，在运行过程中无须释放。然而，用 new 在程序运行时动态创建的堆对象，则必须由程序用 delete 显式删除。如果动态生成的对象不再需要使用也不用 delete 删除，会使得这部分空间始终不能被其他对象利用，造成内存资源的泄漏。&lt;/p&gt;
&lt;p&gt;有时确实需要在&lt;code&gt;不同类之间转移堆对象的归属&lt;/code&gt;。例如，如果一个函数需要返回一个对象，为了避免复制构造函数因传递返回值被调用(因为大对象的复制构造会有较大开销) ，可以在函数内用 new 建立该对象，再将该对象的地址返回，但这就要求调用这个函数的类确保这个返回的堆对象最后被删除。每当遇到这种情况，都应当在函数的注释中明确指出，函数的调用者应当负责删除函数所返回的堆对象。这实际上是类的对外接口约定的一部分，不过能否正确履行不由编译器来检查，而需完全由编程者来保证。&lt;/p&gt;
&lt;p&gt;解决动态对象的管理问题，也可以借助于&lt;code&gt;共享指针&lt;/code&gt;。共享指针是一种具有指针行为的特殊的类，它会在&lt;code&gt;指向一个堆对象的所有指针都不再有效时，自动将其删除&lt;/code&gt;。虽然使用共享指针要付出一定的效率代价，但安全性很好，容易使用。&lt;/p&gt;
&lt;h3 id=&#34;cosnt_cast&#34;&gt;cosnt_cast&lt;a title=&#34;#cosnt_cast&#34; href=&#34;#cosnt_cast&#34;&gt;&lt;/a&gt;&lt;/h3&gt;
&lt;p&gt;&lt;code&gt;const_cast&lt;/code&gt; 只用于将&lt;code&gt;常指针转换为普通指针，将常引用转换为普通引用&lt;/code&gt;，而不用来将常对象转换为普通对象，因为这是没有意义的。因为对象(而非引用)的转换会生成对象的副本，而使用常对象本来就可以直接生成普通对象的副本.&lt;/p&gt;
&lt;figure class=&#34;highlight c++&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;8&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;9&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;10&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;11&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;#&lt;span class=&#34;keyword&#34;&gt;include&lt;/span&gt; &lt;span class=&#34;string&#34;&gt;&amp;lt;iostream&amp;gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;using&lt;/span&gt; &lt;span class=&#34;keyword&#34;&gt;namespace&lt;/span&gt; std;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;function&#34;&gt;&lt;span class=&#34;type&#34;&gt;int&lt;/span&gt; &lt;span class=&#34;title&#34;&gt;main&lt;/span&gt;&lt;span class=&#34;params&#34;&gt;()&lt;/span&gt; &lt;/span&gt;&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&lt;span class=&#34;type&#34;&gt;int&lt;/span&gt; a = &lt;span class=&#34;number&#34;&gt;100&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&lt;span class=&#34;type&#34;&gt;const&lt;/span&gt; &lt;span class=&#34;type&#34;&gt;int&lt;/span&gt;* ptr = &amp;amp;a;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&lt;span class=&#34;comment&#34;&gt;//*ptr = 88;&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&lt;span class=&#34;type&#34;&gt;int&lt;/span&gt;* q = &lt;span class=&#34;built_in&#34;&gt;const_cast&lt;/span&gt;&amp;lt;&lt;span class=&#34;type&#34;&gt;int&lt;/span&gt;* &amp;gt;(ptr);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	*q = &lt;span class=&#34;number&#34;&gt;88&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&lt;span class=&#34;keyword&#34;&gt;return&lt;/span&gt; &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
&lt;h2 id=&#34;知识点:&#34;&gt;知识点:&lt;a title=&#34;#知识点:&#34; href=&#34;#知识点:&#34;&gt;&lt;/a&gt;&lt;/h2&gt;
&lt;ol&gt;
&lt;li&gt;
&lt;p&gt;指针取值两种方式&lt;/p&gt;
 &lt;figure class=&#34;highlight c++&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;8&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;9&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;function&#34;&gt;&lt;span class=&#34;type&#34;&gt;int&lt;/span&gt; &lt;span class=&#34;title&#34;&gt;main&lt;/span&gt;&lt;span class=&#34;params&#34;&gt;()&lt;/span&gt; &lt;/span&gt;&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;type&#34;&gt;int&lt;/span&gt; nums[] = &amp;#123;&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;2&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;3&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;4&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;5&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;6&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;7&lt;/span&gt;&amp;#125;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;type&#34;&gt;int&lt;/span&gt;* p = &amp;amp;nums[&lt;span class=&#34;number&#34;&gt;4&lt;/span&gt;];&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;cout &amp;lt;&amp;lt; p[&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;] &amp;lt;&amp;lt; endl;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;cout &amp;lt;&amp;lt; p[&lt;span class=&#34;number&#34;&gt;-2&lt;/span&gt;] &amp;lt;&amp;lt; endl;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;cout &amp;lt;&amp;lt; *(p + &lt;span class=&#34;number&#34;&gt;2&lt;/span&gt;) &amp;lt;&amp;lt; endl;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;return&lt;/span&gt; &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
&lt;/li&gt;
&lt;li&gt;
&lt;p&gt;常引用指向的对象可以是常量, &lt;code&gt;const int&amp;amp; a = 1;&lt;/code&gt;&lt;/p&gt;
