t;span class=&#34;line&#34;&gt;	cout &amp;lt;&amp;lt; &lt;span class=&#34;number&#34;&gt;-1&lt;/span&gt; &amp;lt;&amp;lt; endl;						&lt;span class=&#34;comment&#34;&gt;// -1&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	cout &amp;lt;&amp;lt;  &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt; &amp;lt;&amp;lt; endl;						&lt;span class=&#34;comment&#34;&gt;// +1&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	cout &amp;lt;&amp;lt; noshowpos;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	cout &amp;lt;&amp;lt; fixed &amp;lt;&amp;lt; &lt;span class=&#34;number&#34;&gt;333.222211&lt;/span&gt; &amp;lt;&amp;lt; endl;	&lt;span class=&#34;comment&#34;&gt;// 显示浮点的默认6位, 超过则是defaultfloat&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	cout &amp;lt;&amp;lt; scientific &amp;lt;&amp;lt; &lt;span class=&#34;number&#34;&gt;333.22221&lt;/span&gt; &amp;lt;&amp;lt; endl;&lt;span class=&#34;comment&#34;&gt;// 科学计数法&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&lt;span class=&#34;comment&#34;&gt;// iomanip&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	cout &amp;lt;&amp;lt; &lt;span class=&#34;built_in&#34;&gt;setw&lt;/span&gt;(&lt;span class=&#34;number&#34;&gt;6&lt;/span&gt;) &amp;lt;&amp;lt; left &amp;lt;&amp;lt; &lt;span class=&#34;built_in&#34;&gt;setfill&lt;/span&gt;(&lt;span class=&#34;string&#34;&gt;&amp;#x27;?&amp;#x27;&lt;/span&gt;) &amp;lt;&amp;lt; &lt;span class=&#34;string&#34;&gt;&amp;quot;||&amp;quot;&lt;/span&gt; &amp;lt;&amp;lt; &lt;span class=&#34;string&#34;&gt;&amp;quot;||&amp;quot;&lt;/span&gt; &amp;lt;&amp;lt; endl; &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&lt;span class=&#34;comment&#34;&gt;// 位宽6个字符, 左对齐, &amp;#x27;?&amp;#x27; 填充&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	cout &amp;lt;&amp;lt; &lt;span class=&#34;built_in&#34;&gt;setprecision&lt;/span&gt;(&lt;span class=&#34;number&#34;&gt;2&lt;/span&gt;) &amp;lt;&amp;lt; &lt;span class=&#34;number&#34;&gt;7.33333&lt;/span&gt; &amp;lt;&amp;lt; endl;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&lt;span class=&#34;comment&#34;&gt;// 两位有效数字&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&lt;span class=&#34;keyword&#34;&gt;return&lt;/span&gt; &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;;  &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
&lt;h3 id=&#34;自定义数据类型&#34;&gt;自定义数据类型&lt;a title=&#34;#自定义数据类型&#34; href=&#34;#自定义数据类型&#34;&gt;&lt;/a&gt;&lt;/h3&gt;
