&gt;						res.v[p * res.j + q] = res.v[p * res.j + q] + v[p * j + r] * m.v[r * m.j + q];&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;					&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;				&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;			&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;			&lt;span class=&#34;built_in&#34;&gt;copy&lt;/span&gt;(res.v.&lt;span class=&#34;built_in&#34;&gt;begin&lt;/span&gt;(), res.v.&lt;span class=&#34;built_in&#34;&gt;end&lt;/span&gt;(), v.&lt;span class=&#34;built_in&#34;&gt;begin&lt;/span&gt;());&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;			cout &amp;lt;&amp;lt; *&lt;span class=&#34;keyword&#34;&gt;this&lt;/span&gt; &amp;lt;&amp;lt; endl;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&lt;span class=&#34;comment&#34;&gt;// 重载()&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&lt;span class=&#34;function&#34;&gt;&lt;span class=&#34;type&#34;&gt;int&lt;/span&gt; &lt;span class=&#34;title&#34;&gt;operator&lt;/span&gt;&lt;span class=&#34;params&#34;&gt;()&lt;/span&gt;&lt;span class=&#34;params&#34;&gt;(&lt;span class=&#34;type&#34;&gt;int&lt;/span&gt; a, &lt;span class=&#34;type&#34;&gt;int&lt;/span&gt; b)&lt;/span&gt; &lt;/span&gt;&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		&lt;span class=&#34;keyword&#34;&gt;return&lt;/span&gt; v[a * j + b];&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;function&#34;&gt;&lt;span class=&#34;type&#34;&gt;int&lt;/span&gt; &lt;span class=&#34;title&#34;&gt;main&lt;/span&gt;&lt;span class=&#34;params&#34;&gt;()&lt;/span&gt; &lt;/span&gt;&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&lt;span class=&#34;type&#34;&gt;int&lt;/span&gt; row1, col1, row2, col2;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	cin &amp;gt;&amp;gt; row1 &amp;gt;&amp;gt; col1;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&lt;span class=&#34;function&#34;&gt;Matrix &lt;span class=&#34;title&#34;&gt;m1&lt;/span&gt;&lt;span class=&#34;params&#34;&gt;(row1, col1)&lt;/span&gt;&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	cin &amp;gt;&amp;gt; m1;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	cin &amp;gt;&amp;gt; row2 &amp;gt;&amp;gt; col2;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&lt;span class=&#34;function&#34;&gt;Matrix &lt;span class=&#34;title&#34;&gt;m2&lt;/span&gt;&lt;span class=&#34;params&#34;&gt;(row2, col2)&lt;/span&gt;&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	cin &amp;gt;&amp;gt; m2;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	cout &amp;lt;&amp;lt; &lt;span class=&#34;built_in&#34;&gt;m1&lt;/span&gt;(row1 / &lt;span class=&#34;number&#34;&gt;2&lt;/span&gt;, col1 / &lt;span class=&#34;number&#34;&gt;2&lt;/span&gt;) &amp;lt;&amp;lt; endl;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	m1 *= m2;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	m1 += m2;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	m1 = m2;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	cout &amp;lt;&amp;lt; m1 &amp;lt;&amp;lt; endl;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;	&lt;span class=&#34;keyword&#34;&gt;return&lt;/span&gt; &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;

 &lt;blockquote&gt;
&lt;p&gt;C++作业&lt;/p&gt;
&lt;/blockquote&gt;
&lt;h2 id=&#34;task2_3&#34;&gt;task2_3&lt;a title=&#34;#task2_3&#34; href=&#34;#task2_3&#34;&gt;&lt;/a&gt;&lt;/h2&gt;
&lt;h3 id=&#34;题目&#34;&gt;题目&lt;a title=&#34;#题目&#34; href=&#34;#题目&#34;&gt;&lt;/a&gt;&lt;/h3&gt;
&lt;p&gt;教材习题4_20：定义满足要求的复数类Complex类&lt;br&gt;
【问题描述】&lt;br&gt;
定义一个复数类Complex，使得下面的代码能够工作。（注：下列代码需放在主函数中。）&lt;br&gt;
Complex c1(3,5);    //用复数3+5i初始化c1&lt;br&gt;
Complex c2=4.5;     &lt;a href=&#34;//xn--4-6c2b230a3lq.xn--5c2-2h2en8bl4w&#34;&gt;//用实数4.5初始化c2&lt;/a&gt;&lt;br&gt;
c1.add(c2);   //将c1与c2相加，结果保存在c1中&lt;br&gt;
c1.show(); //将c1输出（这时的结果应该是7.5+5i）&lt;br&gt;
【输入形式】&lt;br&gt;
无&lt;br&gt;
【输出形式】&lt;br&gt;
7.5+5i&lt;br&gt;
【样例输入】&lt;br&gt;
【样例输出】&lt;br&gt;
7.5+5i&lt;/p&gt;
